					private function loadXML():void
{
	//instantiates loader, loads the XML data, runs onComplete function
	_loader = new URLLoader();
	_loader.load(new URLRequest("resources/songs.xml"));
	_loader.addEventListener(Event.COMPLETE, onXMLLoad);
}
private function onXMLLoad(evt:Event):void
{
	var xmlData:XML = new XML(evt.currentTarget.data);

	tracks:Array = [];

		for each(var track:XML in xmlData.song)
		{
			tVO:TracksVO = new TracksVO();
			tVO.artist = track.artist;
			tVO.title = track.title;

			tracks.push(tVO);
		}
}

