					private var _snd:Sound;
private var _sc:SoundChannel = new SoundChannel();
private var _st:SoundTransform;
private var _pos:int;

var playBtn:Button = new Button();
var pauseBtn:Button = new Button();

playBtn.addEventListener(MouseEvent.CLICK, onPlayClick);
pauseBtn.addEventListener(MouseEvent.CLICK, onPauseClick);

private function loadMP3(path:String):void
{
	_snd = new Sound();
	_snd.load(new URLRequest(path));
	_snd.addEventListener(Event.COMPLETE, playSound);
}
private function playSound(evt:Event):void
{
	_sc = _snd.play(_pos,0,_st);
}
private function onPlayClick(evt:MouseEvent):void
{
	_st = new SoundTransform(1);
	playSound();
}
private function onPauseClick(evt:MouseEvent):void
{
	_pos = _sc.position;
	_sc.stop();
}

